UPDATE `creature_ai_texts` SET `entry`='-662', `content_default`='That fates should be so cruel as to permit us only this after a thousand years apart...', `content_loc1`=NULL, `content_loc2`='Ce doît être un destin bien cruel que de nous permettre ceci après mille ans d\'absence...', `content_loc3`=NULL, `content_loc4`='命运是如此残酷，它让我们在分别了数千年之后才能重逢……', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`='И, в конце концов, это Рагнарос дал мне имя, когда я возродился в облике служителя огня. Он назвал меня Латориком. Латориком Черным.', `sound`='0', `type`='0', `language`='0', `emote`='0', `comment`='3644' WHERE (`entry`='-662');
