UPDATE `creature_ai_texts` SET `entry`='-143941', `content_default`='Citizens and allies of Stormwind, on this day, history has been made. $N has laid waste to that which had attempted to usurp the rule of the kingdom. Gather round and join me in honoring our heroes.', `content_loc1`=NULL, `content_loc2`='Citoyens et alliés de Stormwind, en ce jour, l\\\'histoire a été écrite. $N has a dévasté ce qui avait tenté d\\\'urusper les dirigeants de note royaume. Approchez et honorez nos héros avec moi.', `content_loc3`=NULL, `content_loc4`='暴风城的公民和盟友，在这一天，历史被改写。$N 结束了那些企图篡夺王国统治的人的生命。集合起来和我一起庆祝我们的英雄。', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='0', `language`='0', `emote`='0', `comment`='ally Onyxia Head yell1' WHERE (`entry`='-143941');
