UPDATE `creature_ai_texts` SET `entry`='-661', `content_default`='Anaya...? Do my eyes deceive me? Is it really you?', `content_loc1`=NULL, `content_loc2`='Anaya...? Est-ce que mes yeux me trompent ? Est-ce vraiment toi ?', `content_loc3`=NULL, `content_loc4`='安娜雅……？我的双眼没有欺骗我吗？真的是你吗？', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`='Обсидион был создан по воле Рагнароса. Обсидион уничтожит орков Черной горы, живущих на Пике Черной горы, и объединит нас с нашими собратьями в огненных недрах.', `sound`='0', `type`='0', `language`='0', `emote`='0', `comment`='3644' WHERE (`entry`='-661');
UPDATE `creature_ai_texts` SET `entry`='-662', `content_default`='That fates should be so cruel as to permit us only this after a thousand years apart...', `content_loc1`=NULL, `content_loc2`='Ce doît être un destin bien cruel que de nous permettre ceci après mille ans d\'absence...', `content_loc3`=NULL, `content_loc4`='命运是如此残酷，它让我们在分别了数千年之后才能重逢……', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`='И, в конце концов, это Рагнарос дал мне имя, когда я возродился в облике служителя огня. Он назвал меня Латориком. Латориком Черным.', `sound`='0', `type`='0', `language`='0', `emote`='0', `comment`='3644' WHERE (`entry`='-662');
UPDATE `creature_ai_texts` SET `entry`='-663', `content_default`='Do you hate me, my love? That I was forced to destroy your living form, that your spirit be released from unhappy bondage.', `content_loc1`=NULL, `content_loc2`='Tu me détestes, mon amour ? J\'ai été forcé de détruire ton enveloppe charnelle, pour que ton esprit soit libéré d\'une malheureuse servitude.', `content_loc3`=NULL, `content_loc4`='我的爱人，你恨我吗？我在万般无奈之下摧毁了你的躯壳，因为只有这样才能让你从痛苦中永久地解脱。', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`='%s прекращает смеяться...', `sound`='0', `type`='0', `language`='0', `emote`='0', `comment`='3644' WHERE (`entry`='-663');
