UPDATE `creature_ai_texts` SET `entry`='-570210', `content_default`='When facing a spellcaster of any kind, this feral beast will be your best friend. Now, let us take a look at something a bit different. This next creature will aid your travels and make your future journeys much easier. Let\'s take a look at a Felsteed.', `content_loc1`=NULL, `content_loc2`='Quand vous serez confronté à un lanceur de sort de n\'importe quelle sorte, cette bête féroce sera votre meilleur ami. À présent, intéressons nous à quelque chose d\'un peu différent. La prochaine créature vous aidera à vous déplacer et rendra vos futurs voyages bien plus faciles : jetons maintenant un œil à un Destrier Corrompu.', `content_loc3`=NULL, `content_loc4`=NULL, `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='0', `language`='33', `emote`='0', `comment`='Undercity Summoning Demonstration - Say 10' WHERE (`entry`='-570210');
