UPDATE `script_texts` SET `entry`='-1000757', `content_default`='Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', `content_loc1`=NULL, `content_loc2`='Aye, Fandral, souvenez vous de ces mots: ne laissez pas votre chagrin guider votre foi. Ces pensées que vous portez... vous conduirons à votre perte. L\'absolution ne peut être obtenue par la vengeance.', `content_loc3`=NULL, `content_loc4`='是的，弗兰多，记住这句话：不要让悲伤扭曲你的信仰。你的这些想法…会让你走进黑暗，暗夜精灵。误入歧途的报复是不能被赦免。', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='0', `language`='0', `emote`='1', `comment`='SAY_CAELESTRASZ_INTRO_4' WHERE (`entry`='-1000757');
