UPDATE `script_texts` SET `entry`='-1000759', `content_default`='We will push him back, Anachronos. This I vow. Uphold your end of this task. Let not your hands falter as you seal our fates behind the barrier.', `content_loc1`=NULL, `content_loc2`='Nous le repousserons, Anachronos, j\'en fais le serment. Accomplissez votre part dans cette tâche. Que nos mains ne tremblent pas lorsque vous scellerez nos destins derrière la barrière !', `content_loc3`=NULL, `content_loc4`='我们会把他推回去，阿纳克洛斯。我发誓。坚持完成这项任务。当你把我们的命运封印在障碍物后，不要让你的双手颤抖。', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='0', `language`='0', `emote`='1', `comment`='SAY_MERITHRA_INTRO_5' WHERE (`entry`='-1000759');
UPDATE `script_texts` SET `entry`='-1000760', `content_default`='Succumb to the endless dream, little ones. Let it comsume you!', `content_loc1`=NULL, `content_loc2`='Laissez le rêve infini vous submerger, petits êtres. Laissez-le vous consumer !', `content_loc3`=NULL, `content_loc4`='臣服在无尽的梦魇中吧，小家伙。让它吞噬你！', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='1', `language`='0', `emote`='22', `comment`='SAY_MERITHRA_ATTACK_1' WHERE (`entry`='-1000760');
UPDATE `script_texts` SET `entry`='-1000761', `content_default`='Anachronos, the diversion will give you an the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', `content_loc1`=NULL, `content_loc2`='Anachronos, cette diversion vous donnera assez de temps, à vous et au jeune druide, pour sceller la porte. Ne faiblissez pas. Et maintenant, voyons comment ils supportent la magie chaotique.', `content_loc3`=NULL, `content_loc4`='阿纳克洛斯，这种转移将给你足够的时间，你的和年轻的德鲁伊，密封门。不减弱。现在，让我们来看看如何支持他们的魔法混乱。', `content_loc5`=NULL, `content_loc6`=NULL, `content_loc7`=NULL, `content_loc8`=NULL, `sound`='0', `type`='0', `language`='0', `emote`='1', `comment`='SAY_ARYGOS_ATTACK_2' WHERE (`entry`='-1000761');
